# méthodes différentes
def nbJourn(journalists)
   puts "Le nombre de journalistes dans la liste est de #{journalists.length}"
end


def nbJournAvecNb(journalists)
 puts "Il y a #{journalists.grep(/[0-9]/).length} de personnes à avoir un numéro dans leur pseudo"
end


def aude(journalists)
   downcase = journalists.map(&:downcase)
   puts "Le nombre de personnes ayant Aude dans leur nom sont de #{downcase.grep(/aude/).count} cas"

end


def startWithMaj(journalists)
   puts "Le nombre de personnes possédant une majuscule au début de leur pseudo est de #{journalists.grep(/^@[A-Z]/).count}"
end


def atLeastOneMaj(journalists)
puts "Le nombre de journaliste qui contiennent au moins une majuscule dans leur pseudo est de #{journalists.grep(/[A-Z]/).count}"
end


def underscore (journalists)
   puts "Il y a #{journalists.grep(/_/).count} undescores dans tous les pseudos confondus"
end


def ordreAlpha (journalists)
   puts "Voici l'ordre alphabétique de la liste !"
   puts journalists.sort
end


def short(journalists)
   puts "Voici l'odre croissant des pseudos de la liste !"
   puts journalists.sort_by(&:length).slice(0..50)
end


def posEpenser(journalists)
   puts "@epenser est en #{journalists.index('@epenser')}ème place dans la liste"
end


#Menu
puts "Salut mon ami ! Bienvenue dans le Big Data Center !"
puts "Alors qu'est-ce qu'il vous ferez plaisir ? "
puts "#################################################################"
puts "1 - Affiche le nombre de journalistes en tout"
puts "2 - Affiche le nombre de journalistes qui ont un numéro dans leur handle"
puts "3 - Affiche les handles contenant le mot Aude"
puts "4 - Affiche le nombre de pseudos commençant par une majuscule"
puts "5 - Affiche le nombre de pseudos contenant au moins une majuscule"
puts "6 - Affiche le nombre d'underscores_ dans tous les pseudos"
puts "7 - Trie la liste par ordre alphabétique"
puts "8 - Trie les 50 handles les plus courts de la liste"
puts "9 - Donne la position de @epenser dans la liste"
puts "(0 pour arrêter le programme)"
puts "##################################################################"

while (1)
   print "> "
   usr=gets.chomp.to_i


   #appels des fonctions
if usr == 1
   nbJourn(journalists)
elsif usr == 2
   nbJournAvecNb(journalists)
elsif usr == 3
   aude(journalists)
elsif usr == 4
   startWithMaj(journalists)
elsif usr == 5
   atLeastOneMaj(journalists)
elsif usr == 6
   underscore(journalists)
elsif usr == 7
   ordreAlpha(journalists)
elsif usr == 8
   short(journalists)
elsif usr == 9
   posEpenser(journalists)
elsif usr == 0
   exit

elsif usr > 9
   puts "Un chiffre plus petit, t'as vu la liste nan ?"
elsif usr < 0
   puts "Aller, soit pas timide, augmente un peu ton chiffre ;)"
end

end